mapper.select:
SELECT


,jingdian.jingdian_name as jingdianName
,jingdian.jingdian_uuid_number as jingdianUuidNumber
,jingdian.jingdian_photo as jingdianPhoto
,jingdian.jingdian_address as jingdianAddress
,jingdian.zan_number as zanNumber
,jingdian.cai_number as caiNumber
,jingdian.jingdian_video as jingdianVideo
,jingdian.jingdian_types as jingdianTypes
,jingdian.jingdian_kucun_number as jingdianKucunNumber
,jingdian.jingdian_new_money as jingdianNewMoney
,jingdian.jingdian_content as jingdianContent
,jingdian.jingdian_delete as jingdianDelete
,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.new_money as newMoney
,yonghu.yonghu_email as yonghuEmail

FROM jingdian_commentback  a
left JOIN jingdian jingdian ON a.jingdian_id = jingdian.id
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



        and a.id in

        #{item}



        and (
            a.jingdian_id = #{params.jingdianId}
        )


        and (
            a.yonghu_id = #{params.yonghuId}
        )


        and a.jingdian_commentback_text like CONCAT('%',#{params.jingdianCommentbackText},'%')


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.reply_text like CONCAT('%',#{params.replyText},'%')


          and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


          and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 




        and a.jingdian_id IS NOT NULL


        and jingdian.jingdian_name like CONCAT('%',#{params.jingdianName},'%')


        and jingdian.jingdian_uuid_number like CONCAT('%',#{params.jingdianUuidNumber},'%')


        and jingdian.jingdian_address like CONCAT('%',#{params.jingdianAddress},'%')


          and jingdian.zan_number >= #{params.zanNumberStart}   


          and jingdian.zan_number <= #{params.zanNumberEnd}   


        and jingdian.zan_number = #{params.zanNumber}


          and jingdian.cai_number >= #{params.caiNumberStart}   


          and jingdian.cai_number <= #{params.caiNumberEnd}   


        and jingdian.cai_number = #{params.caiNumber}


        and jingdian.jingdian_video like CONCAT('%',#{params.jingdianVideo},'%')


        and jingdian.jingdian_types = #{params.jingdianTypes}



          and jingdian.jingdian_kucun_number >= #{params.jingdianKucunNumberStart}   


          and jingdian.jingdian_kucun_number <= #{params.jingdianKucunNumberEnd}   


        and jingdian.jingdian_kucun_number = #{params.jingdianKucunNumber}


          and jingdian.jingdian_new_money >= #{params.jingdianNewMoneyStart}   


          and jingdian.jingdian_new_money <= #{params.jingdianNewMoneyEnd}   


        and jingdian.jingdian_content like CONCAT('%',#{params.jingdianContent},'%')


          and jingdian.jingdian_delete >= #{params.jingdianDeleteStart}   


          and jingdian.jingdian_delete <= #{params.jingdianDeleteEnd}   


        and jingdian.jingdian_delete = #{params.jingdianDelete}


          and UNIX_TIMESTAMP(jingdian.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(jingdian.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


          and yonghu.new_money >= #{params.newMoneyStart}   


          and yonghu.new_money <= #{params.newMoneyEnd}   


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


order by a.${params.sort} ${params.order}
