mapper.select:
SELECT



FROM shangpin  a



        and a.id in

        #{item}



        and a.shangpin_name like CONCAT('%',#{params.shangpinName},'%')


        and a.shangpin_uuid_number like CONCAT('%',#{params.shangpinUuidNumber},'%')


        and a.shangpin_address like CONCAT('%',#{params.shangpinAddress},'%')


          and a.zan_number >= #{params.zanNumberStart}   


          and a.zan_number <= #{params.zanNumberEnd}   


        and a.zan_number = #{params.zanNumber}


          and a.cai_number >= #{params.caiNumberStart}   


          and a.cai_number <= #{params.caiNumberEnd}   


        and a.cai_number = #{params.caiNumber}


        and a.shangpin_types = #{params.shangpinTypes}


          and a.shangpin_kucun_number >= #{params.shangpinKucunNumberStart}   


          and a.shangpin_kucun_number <= #{params.shangpinKucunNumberEnd}   


        and a.shangpin_kucun_number = #{params.shangpinKucunNumber}


          and a.shangpin_new_money >= #{params.shangpinNewMoneyStart}   


          and a.shangpin_new_money <= #{params.shangpinNewMoneyEnd}   


          and a.shangpin_clicknum >= #{params.shangpinClicknumStart}   


          and a.shangpin_clicknum <= #{params.shangpinClicknumEnd}   


        and a.shangpin_clicknum = #{params.shangpinClicknum}


        and a.shangpin_content like CONCAT('%',#{params.shangpinContent},'%')


          and a.shangpin_delete >= #{params.shangpinDeleteStart}   


          and a.shangpin_delete <= #{params.shangpinDeleteEnd}   


        and a.shangpin_delete = #{params.shangpinDelete}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



order by a.${params.sort} ${params.order}
