mapper.select:
SELECT



FROM jiudian  a



        and a.id in

        #{item}



        and a.jiudian_name like CONCAT('%',#{params.jiudianName},'%')


        and a.jiudian_uuid_number like CONCAT('%',#{params.jiudianUuidNumber},'%')


        and a.jiudian_address like CONCAT('%',#{params.jiudianAddress},'%')


          and a.zan_number >= #{params.zanNumberStart}   


          and a.zan_number <= #{params.zanNumberEnd}   


        and a.zan_number = #{params.zanNumber}


          and a.cai_number >= #{params.caiNumberStart}   


          and a.cai_number <= #{params.caiNumberEnd}   


        and a.cai_number = #{params.caiNumber}


        and a.jiudian_video like CONCAT('%',#{params.jiudianVideo},'%')


        and a.jiudian_types = #{params.jiudianTypes}


          and a.jiudian_kucun_number >= #{params.jiudianKucunNumberStart}   


          and a.jiudian_kucun_number <= #{params.jiudianKucunNumberEnd}   


        and a.jiudian_kucun_number = #{params.jiudianKucunNumber}


          and a.jiudian_new_money >= #{params.jiudianNewMoneyStart}   


          and a.jiudian_new_money <= #{params.jiudianNewMoneyEnd}   


        and a.jiudian_content like CONCAT('%',#{params.jiudianContent},'%')


          and a.jiudian_delete >= #{params.jiudianDeleteStart}   


          and a.jiudian_delete <= #{params.jiudianDeleteEnd}   


        and a.jiudian_delete = #{params.jiudianDelete}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



order by a.${params.sort} ${params.order}
